import TableRow from "@material-ui/core/TableRow";
import {PaddedTableCell, StyledTableHead} from "../../styling/StyledComponents";
import {TableSortLabel} from "@material-ui/core";
import * as React from "react";
import {connect} from "react-redux";
import * as actionCreators from "../../store/actions/globalActions";

interface ISearchResultsTableHeadProps {
    orderBy: any,
    on_request_sort: (event: any, property: any) => void,
}
const TableHeadWithSort: React.FC<ISearchResultsTableHeadProps> = (props) => {
    const createSortHandler = (property: any) => (event: React.MouseEvent<unknown>) => {
        props.on_request_sort(event, property);
    }
    return(
<StyledTableHead>
    <TableRow>
        <PaddedTableCell>
            <TableSortLabel
                IconComponent={props.orderBy['title'].icon}
                active={true}
                direction={props.orderBy['title'].sort}
                onClick={createSortHandler('title')}
            >
                Movie
            </TableSortLabel>
        </PaddedTableCell>
        <PaddedTableCell
            align="left"
        >
            <TableSortLabel
                IconComponent={props.orderBy['rating'].icon}
                active={true}
                direction={props.orderBy['rating'].sort}
                onClick={createSortHandler('rating')}
            >
                Rating
            </TableSortLabel>
        </PaddedTableCell>
        <PaddedTableCell align="left">Runtime</PaddedTableCell>
        <PaddedTableCell align="left">Genres</PaddedTableCell>
        <PaddedTableCell align="left">Crew</PaddedTableCell>
    </TableRow>
</StyledTableHead>
)}

function mapStateToProps(state: any) {
    return {
        orderBy: state.orderBy,
    }
}

function mapDispatchToProps(dispatch: any) {
    return {
        on_request_sort: (event: any, property: any) => dispatch(actionCreators.sortByTitleOrRating(event, property))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(TableHeadWithSort);
