import {StyledChip} from "./StyledComponents";
import {Avatar} from "@material-ui/core";
import * as React from "react";

const genreList = ['action', 'adventure', 'biography', 'comedy', 'crime', 'drama', 'fantasy', 'history', 'horror', 'mystery', 'sci-fi', 'thriller', 'war']

interface IGenresProps {
    genre: string
}
export const GenresComponent: React.FC<IGenresProps> = (props) => {

    const genreIcon = genreList.indexOf(props.genre) > -1 ? props.genre : "other"
    return (<StyledChip icon={<Avatar src={require(`../../icons/${genreIcon}.png`)}/>}
                label={props.genre}/>)
}
