import {Link} from "react-router-dom";
import withStyles from "@material-ui/core/styles/withStyles";
import {Rating} from "@material-ui/lab";

export const Page = styled.div`
    width:80%;
    margin: auto;
    padding: 1rem;

    @media only screen and (max-width: 500px) {
