    width: 100%;

       @media screen and (max-height: 1200px) {
    margin: 0rem;
}
`

export const SearchIconContainer = styled.div`
   display: flex;
   padding-right: 1rem;
`
export const SearchBar = styled(TextField)`
    display: flex;
