    `

/*MOVIE PAGE*/

export const MovieOverviewContainer = styled.div`
display: block;
padding-top:1.5rem;
`

export const MovieRatingContainer = styled.div`
     display: inline-block;

`
export const RatingLeft = styled.div`
    padding-right: .25rem;
