import * as React from 'react';
import styled from "styled-components";
import {Button, Dialog, DialogActions, DialogContent, DialogTitle} from '@material-ui/core';
import {mainColor} from '../../styling/mainTheme'

const Footer = () => {
    const [open, setOpen] = React.useState(false);

    const handleClick = () => {
        setOpen(true);
    }

    const handleClose = () => {
        setOpen(false);
    }

    return (
        <>

        <FooterContainer>
            <FooterButton onClick={handleClick}> About </FooterButton>
            <Dialog
                open={open}
                onClose={handleClose}
            >
                <DialogTitle>
                    About
                </DialogTitle>
                <DialogContent>
                Built by Divya Prem. This WebApp presents results from a Movie API provided by IT.com. Technologies used include React, TypeScript, Redux, & Material UI Components. Credits to Icon8 for the Genre Icons.
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose} color="primary" autoFocus>
                        Close
                    </Button>
                </DialogActions>
                </Dialog>
        </FooterContainer>
            </>
    )
}

export default Footer

const FooterButton = styled.button`
    background: none;
    border: none;
    color: white;
    // padding: .5rem 2rem 0 2rem;
    font-size: 1rem;
`


const FooterContainer = styled.div`
    padding: .5rem;
    background-color: ${mainColor};
    height: 1rem;
    color: white;
    text-align: right;
`
