import {PaddedTableCell, StyledTableRow} from "../../styling/StyledComponents";
import {TableRating} from "../SharedComponents/Ratings";
import {RuntimeGrid} from "../SharedComponents/Runtime";
import {TableBody} from "@material-ui/core";
import * as React from "react";
import {useHistory, withRouter} from "react-router";
import {connect} from "react-redux";

interface IStyledTableBodyProps {
    movieSummaries: any
}

const StyledTableBody: React.FC<IStyledTableBodyProps> = (props) => {
    let history = useHistory();


    const handleRowClick = (id: string) => {
        console.log(id)
        history.push(`/movie/${id}`)
    }
    return (
        <TableBody>
        {props.movieSummaries.map((movie: any) => (

            <StyledTableRow key={movie.movieId} onClick={() => handleRowClick(movie.movieId)}>
                <PaddedTableCell component="th" scope="row">{movie.title}</PaddedTableCell>
                <PaddedTableCell align="left"><TableRating rating={movie.rating}/></PaddedTableCell>
                <PaddedTableCell align="left"><RuntimeGrid runtime={movie.runtime}/></PaddedTableCell>
                <PaddedTableCell>{movie.genres.map((it: React.ReactNode) => <span>{it}</span>)} </PaddedTableCell>
            </StyledTableRow>

        ))}
    </TableBody>)
}

function mapStateToProps(state: any) {
    return {
        movieSummaries: state.movieSummaries
    }
}

function mapDispatchToProps(dispatch: any) {
    return {
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(StyledTableBody);
