import {StyledChip} from "../../styles/StyledComponents";
import {Avatar} from "@material-ui/core";
import * as React from "react";

const genreList = [
    'action',
    'adventure',
    'biography',
    'comedy',
    'crime',
    'drama',
    'fantasy',
    'history',
    'horror',
    'mystery',
    'sci-fi',
    'thriller',
    'war']

interface IGenresProps {
    genre: string
}
export const GenreChip: React.FC<IGenresProps> = (props) => {
    const genreIcon = genreList.indexOf(props.genre) > -1 ? props.genre : "genre"
    return (<StyledChip icon={<Avatar src={require(`../styles/icons`)}/>}
                label={props.genre}/>)
}
