import * as React from "react"
import {Grid} from "@material-ui/core";
import { NoWrap} from "../../styling/StyledComponents";

interface RuntimeProps {
    runtime: number
}

export const RuntimeGrid: React.FC<RuntimeProps> = (props: { runtime: number }) => {
    const hour = Math.floor(props.runtime / 60);
    const min = Math.floor(props.runtime % 60);
    return (
        <>
            <Grid container>
                <Grid item xs={12} sm={12} md={6} lg={3}><NoWrap>{hour > 0 ? `${hour} hr${hour > 1 ? "s" : ""} ` : ""}</NoWrap></Grid>
                <Grid item xs={12} sm={12} md={6} lg={3}><NoWrap>{min > 0 ? `${min} min${min > 1 ? "s" : ""}` : ""}</NoWrap></Grid>
            </Grid>
        </>
    );
}

export const Runtime: React.FC<RuntimeProps> = (props: { runtime: number }) => {
    const hour = Math.floor(props.runtime / 60);
    const min = Math.floor(props.runtime % 60);
    return (
        <>
            <>{hour > 0 ? `${hour} hr${hour > 1 ? "s" : ""} ` : ""}</>
                <>{min > 0 ? `${min} min${min > 1 ? "s" : ""}` : ""}</>

        </>
    );
}
