import {PaddedTableCell, StyledChip, StyledTableRow} from "../../styles/StyledComponents";
import {GridRating} from "../SharedComponents/Ratings";
import {RuntimeGrid} from "../SharedComponents/Runtime";
import {Avatar, TableBody} from "@material-ui/core";
import * as React from "react";
import {useHistory} from "react-router";
import {connect} from "react-redux";
import {GenreChip} from "../SharedComponents/GenreChip";

interface IStyledTableBodyProps {
    movieSummaries: any
}

const SearchResultsTableBody: React.FC<IStyledTableBodyProps> = (props) => {
    let history = useHistory();
    const handleRowClick = (id: string) => {
        console.log(id)
        history.push(`/movie/${id}`)
    }

    return (
        <TableBody>
            {props.movieSummaries.map((movie: any) => (
                <StyledTableRow
                    key={movie.movieId}
                    onClick={() => handleRowClick(movie.movieId)}
                >
                    <PaddedTableCell>
                        {movie.title}
                        <br/>
                        {movie.genres.map((genre: string, i: number) =>
                                <GenreChip key={i} size="small" genre={genre}/>
                            )}
                    </PaddedTableCell>
                    <PaddedTableCell align="left"><GridRating rating={movie.rating}/></PaddedTableCell>
                    <PaddedTableCell align="left"><RuntimeGrid runtime={movie.runtime} page="search"/></PaddedTableCell>
                    <PaddedTableCell>{movie.crew.join(", ")} </PaddedTableCell>

                </StyledTableRow>

            ))}
        </TableBody>)
}

function mapStateToProps(state: any) {
    return {
        movieSummaries: state.movieSummaries
    }
}

function mapDispatchToProps(dispatch: any) {
    return {}
}

export default connect(mapStateToProps, mapDispatchToProps)(SearchResultsTableBody);
