import {
    Genres,
    MovieOverviewContainer,
    PaddedTableCell,
    StyledChip,
    StyledTableRow
} from "../../styling/StyledComponents";
import {TableRating} from "../SharedComponents/Ratings";
import {RuntimeGrid} from "../SharedComponents/Runtime";
import {Avatar, TableBody} from "@material-ui/core";
import * as React from "react";
import {useHistory, withRouter} from "react-router";
import {connect} from "react-redux";

interface IStyledTableBodyProps {
    movieSummaries: any
}

const StyledTableBody: React.FC<IStyledTableBodyProps> = (props) => {
    let history = useHistory();


    const handleRowClick = (id: string) => {
        console.log(id)
        history.push(`/movie/${id}`)
    }
    return (
        <TableBody>
        {props.movieSummaries.map((movie: any) => (

            <StyledTableRow key={movie.movieId} onClick={() => handleRowClick(movie.movieId)}>
                <PaddedTableCell component="th" scope="row">
                    {movie.title} <br/>
                    <Genres>
                        {movie.genres.map((genre: string, i: number) => {
                                const genreIcon = ['action', 'adventure', 'biography', 'comedy', 'crime', 'drama', 'fantasy', 'history', 'horror', 'mystery', 'sci-fi', 'thriller', 'war'].indexOf(genre) > -1 ? genre : "other"
                                return (
                                    <StyledChip size="small" key={i} icon={<Avatar src={require(`../../icons/${genreIcon}.png`)}/>}
                                                label={genre}/>  )
                            }
                        )}
                    </Genres>
                </PaddedTableCell>
                <PaddedTableCell align="left"><TableRating rating={movie.rating}/></PaddedTableCell>
                <PaddedTableCell align="left"><RuntimeGrid runtime={movie.runtime}/></PaddedTableCell>
                <PaddedTableCell>{movie.genres.join(" ")} </PaddedTableCell>
                <PaddedTableCell>{movie.crew.join(" ")} </PaddedTableCell>

            </StyledTableRow>

        ))}
    </TableBody>)
}

function mapStateToProps(state: any) {
    return {
        movieSummaries: state.movieSummaries
    }
}

function mapDispatchToProps(dispatch: any) {
    return {
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(StyledTableBody);
