import styled from "styled-components";
import withStyles from "@material-ui/core/styles/withStyles";
import {iconColor, subtleIconColor, subtleTextColor} from "./mainTheme";
import {Rating} from "@material-ui/lab";
import {Chip} from "@material-ui/core";
import {Link} from "react-router-dom";

export const MovieOverviewContainer = styled.div`
display: block;
padding-top:1.5rem;
`

export const MovieRatingContainer = styled.div`
     display: inline-block;

`
export const RatingLeft = styled.div`
    padding-right: .25rem;
    display: inline-block;
    vertical-align:middle;
    font-size:1.5rem;

`
export const RatingRight = styled.div`
    display: inline-block;
    vertical-align:middle;
    padding-right: 1rem;

`

export const StyledRating = withStyles({
    iconFilled: {
        color:`${iconColor}`
    },
    iconEmpty: {
        color:`${subtleIconColor}`
    }
})(Rating);

export const Votes = styled.div`
  font-size: .6rem;
  padding-left: .25rem;
  text-align: left;
  margin-top:-.3rem;

`
export const MovieDetails = styled.div`
display: inline-block;
vertical-align:middle;
padding-left: 1rem;
`
export const Detail = styled.div`
    padding-right: 1.5rem;
    display: inline-block;
`

export const Genres = styled.div`
  padding: 2rem .5rem;
`

export const CrewContainer = styled.div`
   padding-top: 1rem; 
   display: block;
    `

export const CrewType = styled.div`
   padding-right: .5rem; 
   padding-top: 1rem;
   color: grey;
    `

export const StyledChip = styled(Chip)`
    margin-right: 1rem;
    margin-bottom:.25rem;
`

export const CrewLink = styled(Link)`
    padding-right: 1rem;
    color: black;
    text-decoration: none;
    &:hover {
  text-decoration: underline;
}
`

export const MovieLink = styled.a`
    padding-right: .5rem;
    text-decoration: none;
    color: black;
    &:hover {
  text-decoration: underline;
  }
  &:visited {
  color: ${subtleTextColor};

}
`

export const Dot = styled.span`
  height: .25rem;
  width: .25rem;
  background-color: #b0bec5;
  border-radius: 50%;
  display: inline-block;
  vertical-align: middle;
  margin-right: .5rem;

  &:last-child{
  background-color:transparent;
  }
`
