import TableContainer from "@material-ui/core/TableContainer";
import {Avatar, Table, TableBody} from "@material-ui/core";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import {Dot, MovieLink, PaddedTableCell, StyledChip, StyledTableRow} from "../../styles/StyledComponents";
import * as React from "react";
import {useHistory, withRouter} from "react-router";
import {connect} from "react-redux";

interface IMovieCrewTableProps {
    crew: any
}

const MovieFilmographyTable: React.FC<IMovieCrewTableProps> = (props) => {
    let history = useHistory();

    const handleRowClick = (id: string) => {
        console.log(id)
        history.push(`/crew/${id}`)
    }

    return (
        <TableContainer>
            <Table>
                <colgroup>
                    <col style={{width: '13%'}}/>
                    <col style={{width: '10%'}}/>
                    <col/>
                </colgroup>
                <TableHead>
                    <TableRow>
                        <PaddedTableCell>
                            Title
                        </PaddedTableCell>
                        <PaddedTableCell/>
                        <PaddedTableCell>
                            Filmography
                        </PaddedTableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {Object.keys(props.crew).map((key: string, i: number) => (
                        <StyledTableRow key={i}>
                            <PaddedTableCell component="th" scope="row" onClick={() => handleRowClick(key)}>{props.crew[key].name}</PaddedTableCell>
                            <PaddedTableCell component="th" scope="row" onClick={() => handleRowClick(key)}>
                                {props.crew[key].roles.map((role: string, i:number) =>
                                    <StyledChip
                                        key={i}
                                        icon={<Avatar src={require(`../../styles/icons`)}/>}
                                        size="small"
                                        label={role}
                                        variant="outlined"/>
                                )}
                            </PaddedTableCell>
                            <PaddedTableCell align="left">{props.crew[key].movies.map((movie: any, i:number) => (
                                <React.Fragment key={i}>
                                <MovieLink key={i} href={`/movie/${movie.id}`}> {movie.title} </MovieLink>
                                <Dot></Dot>
                                </React.Fragment>
                            ))}</PaddedTableCell>
                        </StyledTableRow>

                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    )
}


function mapStateToProps(state: any) {
    return {
        crew: state.crew
    }
}

function mapDispatchToProps(dispatch: any) {
    return {
    }
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(MovieFilmographyTable));
