import styled from "styled-components";
import {Grid, TableCell, TableHead, TableRow} from "@material-ui/core";
import {tableBodyAlternateRowColor} from "./mainTheme";
import withStyles from "@material-ui/core/styles/withStyles";

export const StyledTableRow = styled(TableRow)`
   &:nth-child(odd) {
        background: ${tableBodyAlternateRowColor};
      }
   &:hover{
   background: #cfd8dc;
   }
`

export const PaddedTableCell = withStyles({
    root: {
        borderBottom: "none"
    }
})(TableCell);

export const RatingNumberGrid = styled(Grid)`
    max-width: 15%;
    padding-right: 2rem;
`

export const RatingContainer = styled.div`

    display: flex;
    flexGrow: 1;
`

export const NoWrap = styled.div`
    white-space: nowrap;
    margin-right:1rem;
    `

export const StyledTableHead = styled(TableHead)`
color: #607d8b;
`
