import * as React from "react"
import {connect} from "react-redux";
import * as actionCreators from "../../store/actions/globalActions";
import {
    Detail, ErrorMessage,
    Genres,
    MovieDetails,
    MovieOverviewContainer,
    Page,
    PageTitle,
    StyledChip
} from "../../styling/StyledComponents";
import {Avatar, Divider} from "@material-ui/core";
import {Runtime} from "../SharedComponents/Runtime";
import {MovieRating} from "../SharedComponents/Ratings";
import MovieCrewTable from "./MovieFilmographyTable";

interface IMovieProps {
    id: string,
    find_movie: (movie: any) => void,
    match: any,
    title: string,
    genres: [],
    year: number,
    runtime: number,
    rating: number,
    votes: number,
    directors: [],
    writers: [],
    crew: any,
    search_movies: (searchTerm: string) => void,
    movieSummaries: [],
    currentPage: number,
    resultsTotal: number,
    change_page: (event: any, newPage: number) => void,
    all_movies: () => void,
    change_rows_per_page: (event: any) => void,
    onRequestSort: (event: any, property: any) => void,
    clear_search_state: () => void,
    apiError: boolean,
    errorMessage: string
}


class Movie extends React.Component<IMovieProps> {
    componentDidMount(): void {

        const id = this.props.match.params.id
        this.props.find_movie(id)
    }

    componentWillUnmount(): void {
        this.props.clear_search_state()
    }


    render() {

        if (this.props.apiError) {
            return <ErrorMessage>{this.props.errorMessage}</ErrorMessage>
        }

        return (
            <Page>
                <PageTitle>
                    {this.props.title}
                </PageTitle>
                <Divider />
                <Divider />
                <MovieOverviewContainer>
                    <MovieRating rating={this.props.rating} votes={this.props.votes}/>
                    <MovieDetails>
                        <Detail>
                            {this.props.year}
                        </Detail>
                        <Detail>
                            <Runtime runtime={this.props.runtime}/>
                        </Detail>
                    </MovieDetails>
                    <Genres>
                        {this.props.genres.map((genre: string, i: number) => {
                                const genreIcon = ['action', 'adventure', 'biography', 'comedy', 'crime', 'drama', 'fantasy', 'history', 'horror', 'mystery', 'sci-fi', 'thriller', 'war'].indexOf(genre) > -1 ? genre : "other"
                                return (
                                    <StyledChip size="small" key={i} 
                                                label={genre}/>  )
                            }
                        )}
                    </Genres>
                </MovieOverviewContainer>
                <Divider />
                <Divider />

     <MovieCrewTable/>


            </Page>
        )
    }
}

function mapStateToProps(state: any) {
    return {
        movieId: state.id,
        title: state.title,
        genres: state.genres,
        year: state.year,
        runtime: state.runtime,
        rating: state.rating,
        votes: state.votes,
        directors: state.directors,
        writers: state.writers,
        apiError: state.apiError,
        errorMessage: state.errorMessage
    }
}

function mapDispatchToProps(dispatch: any) {
    return {
        find_movie: (movie: string) => dispatch(actionCreators.findMovie(movie)),
        clear_search_state: () => dispatch(actionCreators.resetState())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Movie);
