import theme from 'styled-theming'


// define background colours for `mode` theme
export const mainColor = theme('mode', {
    light: '#607d8b',
    dark: 'black'
});
// define text color for `mode` theme
export const textColor = theme('mode', {
    light: 'black',
    dark: 'white'
});

export const subtleTextColor = theme('mode', {
    light: '#37474f',
    dark: 'white'
});

export const tableBodyAlternateRowColor = theme('mode', {
    light: '#eff2f3',
    dark: 'darkgray'
})

export const iconColor = '#607d8b'

export const subtleIconColor = '#cfd8dc'

export const bannerIconColor = 'white'
export const pageIconColor = '#607d8b'
